<template>
    <div  id="formulario" class="flex   w-full ">
        <div class="w-1/2 columna">
        <!-- TITULO  -->
        <div class="flex flex-col mb-8 titulo">
            <label for="titulo" class="uppercase mb-2 font-bold text-left text-xl label">
                TÃ­tulo
            </label>
            <div class="flex justify-start  value">
                <input id="titulo" type="text" class="pl-4 h-10 border" name="titulo" v-model="titulo">
                
            </div>
        </div>

        <!-- PREDESCRIPCION  -->
        <div class="flex flex-col mb-8 local">
            <label for="predescripcion" class="uppercase mb-2 font-bold text-left text-xl label">
                Predescripcion
            </label>
            <div class="flex justify-start  value">                  
                <textarea id="predescripcion" class="p-4 h-20 border" v-model="predescripcion" name="predescripcion" rows="4" cols="50">                       
                </textarea>               
            </div>
        </div>

        <!-- DESCRIPCION  -->
        <div class="flex flex-col mb-8 local">
            <label for="descripcion" class="uppercase mb-2 font-bold text-left text-xl label">
                descripcion
            </label>
            <div class="flex justify-start  value">                  
                <textarea id="descripcion" class="p-4 h-36 border" v-model="descripcion" name="descripcion" rows="4" cols="50">                       
                </textarea>               
            </div>
        </div>

        <!-- PRECIO  -->
        <div class="flex flex-col mb-8 titulo">
            <label for="precio" class="uppercase mb-2 font-bold text-left text-xl label">
                PRECIO
            </label>
            <div class="flex justify-start  value">
                <input id="precio" type="text" class="pl-4 h-10 border" name="precio" v-model="precio">                    
            </div>
        </div>

        <!-- ENVIO  -->
        <div class="flex flex-col mb-8 local">
            <label for="local" class="uppercase mb-2 font-bold text-left text-xl label">
                Envio
            </label>
            <div class="flex justify-start  value">
                <input id="envio" type="text" class="pl-4 h-10 border" name="envio" v-model="envio">                  
            </div>
        </div>
        </div>
        <div class="w-1/2 columna">
        

        <!-- IMAGENES  -->
        <div class="flex flex-col mb-8 local">
            <label class="uppercase mb-2 font-bold text-left text-xl label">
                Imagenes
            </label>
            
            <div class="flex justify-between mb-4 value" v-for="(imagen,index) in images" :key="index" >
                <div class="w-2/3 pr-8 Url">
                    <input  id="image" type="text" class="pl-4 w-full h-10  border" :name="index" v-model="images[index]">
                </div>
                <div class=" w-1/3 h-36 img">
                    <img v-if="images[index]!=''" :src="images[index]" class="h-full w-full object-cover" alt="Ups,no exite imagen,por favor verique url">
                    <img v-else class="h-full w-full object-cover"  src="data:image/png;base64," alt="Sin imagen">
                </div>
                
                                    
            </div>
        </div>

        <!-- BOTONES  -->
        <div class="flex justify-between  mb-8 local">
            <button @click="crearProducto()" class="text-bold mr-2 uppercase w-1/3 flex items-center justify-center rounded h-9 bg-lime-600 hover:bg-lime-700 text-white  hover:scale-105 ease-in duration-300  guardar">
                CREAR
            </button>            

            <button @click="volver()" class="text-bold mr-2 uppercase w-1/3 flex items-center justify-center rounded h-9 bg-red-custom text-white  hover:scale-105 ease-in duration-300  hover:bg-red-600 guardar">
                CANCELAR
            </button>
        </div>
        
        </div>
    </div>
</template>

<script>
import axios from 'axios'
import { mapGetters } from 'vuex'

export default {
    name:'NewProduct',
    data(){
        return{
            titulo:'',
            predescripcion:'',
            descripcion:'',
            precio:0,
            envio:0,
            images:['','','','']
        }
    },
    computed:{
        ...mapGetters('Auth',{
            user:'getSesion'
        })
    },
    methods:{ 
        volver(){
            this.$router.go(-1)
        },      
        async crearProducto(){
            
            let newProduct ={                
                titulo:this.titulo,        
                local:this.user.username,
                predescripcion:this.predescripcion,
                descripcion:this.descripcion,
                precio:this.precio,
                envio:this.envio,
                images:this.images,              
            }

            let url ='/new-product'
             const response = await axios.post(url,newProduct)       
             const data = await response.data 
            
            if(data){
                this.$router.push({name:'products'})
            }
        }        

    }
    
}
</script>
